mapper.select:
SELECT id, name, password, email, status, created, last_login, description, avatar
FROM user
WHERE name = #{name}

mapper.update:
UPDATE user
SET last_login = now()
WHERE name = #{name}

UPDATE user
SET password = #{password}
WHERE name = #{name}

UPDATE user
SET email = #{email}, description = #{description}
WHERE name = #{name}

UPDATE user
SET avatar = #{avatar}
WHERE name = #{name}
